mapper.select:
select p.*,pr.id as prId,pr.create_date as prCreateDate,pr.win_bid_state from project p left join project_register pr on pr.project_id = p.id where 1=1
and p.state = #{project.state}
and p.id not in ( select project_id from project_register where member_id = #{memberId} and (state =0 or state=1) ) and (Now() BETWEEN p.senroll_start_date AND p.enroll_end_date)
and pr.member_id = #{memberId} and pr.state = #{state}
group by p.id order by p.update_date desc

select p.* from project p left join review_info ri on ri.project_id=p.id left join win_bid wb on ri.id=wb.win_bid_id where 1=1
and p.state = #{project.state}
and wb.member_id=#{memberId}
and Now() >= p.enroll_end_date and (p.state=1 or p.state=2)
and (p.review_progress is not null and p.review_progress>0)
order by p.update_date desc

select p.*,pr.win_bid_state from project p left join project_register pr on pr.project_id = p.id where 1=1
and p.state=#{state}
and p.project_name=#{projectName}
and pr.member_id=#{projectRegisters.member.id}
order by p.update_date desc
