mapper.select:
select p.*,pr.id as prId,pr.create_date as prCreateDate,pr.win_bid_state from project p left join project_register pr on pr.project_id = p.id where 1=1
and p.state = #{project.state}
and p.is_audit = 1 and p.id not in ( select project_id from project_register where member_id = #{memberId} and (state =0 or state=1 or state=2 or state=3 or state=4 or state=5 ) ) and (Now() BETWEEN p.senroll_start_date AND p.enroll_end_date)
and pr.member_id = #{memberId} and pr.state = #{state}
and (p.state=1 or p.state=2)
group by p.id order by p.update_date desc

select
p.*,pr.win_bid_state
from
	project p
left join project_register pr on pr.project_id = p.id
where
1=1

	and p.state=#{state}


	and p.project_name=#{projectName}




			and pr.member_id = #{projectRegisters.member.id}



		and pr.state = #{projectRegisters.state}


order by
p.update_date desc
